// hash from https://www.shadertoy.com/view/XlGcRh
vec3 hash31(float p)
{
   vec3 p3 = fract(vec3(p,p,p) * vec3(.1031, .1030, .0973));
   p3 += dot(p3, p3.yzx+33.33);
   return fract((p3.xxy+p3.yzz)*p3.zyx);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    for (int i = 0; i < 200; i++) {
        vec3 rand = hash31(float(i));
        vec2 center = rand.xy;
        float shine = rand.z;
        float sd = 0.01 - length(fragCoord/iResolution.x - center);
        fragColor += (1. + sin(iTime * rand.z * 5.)) * .5 * shine * smoothstep(0., 0.01, sd);
    }
}

