float instrument1(int n, float t, float t0) {
    if (t < t0) {
    float freq = 440. * p * p * pow(p, float(n));
        float f = 0., a = abs(sin(t))/100.;
        for (float k = 1.; k < 4.; k++) {
            f += a * sin(tau * mod(freq  * t, 1.)) * exp(30.0*(t-t0)) ;
            a /= 2.;
        }
        return f;

    }
    else {
        float freq = 440. * p * p * pow(p, float(n));
        float f = 0., a = 0.1;
        for (float k = 1.; k < 4.; k++) {
            f += a * sin(tau * mod(freq * k * t, 1.)) * exp(-3.0*(t-t0)) ;
            a /= 2.;
        }
        return f;
    }
}

//from iq's pirate https://www.shadertoy.com/view/ldXXDj
float instrument2(int n, float t, float t0) {
    if (t < t0) {
     float dt = t0-t;
        float freq = 440. * p * p * pow(p, float(n));
        float ph = 1.0;

        ph *= sin(6.283185*freq*dt*2.0);
        ph *= 0.5+0.5*max(0.0,5.0-0.01*freq);
        ph *= exp(-dt*freq*0.2);

        float f = 0.;
        f += 0.001*sin(1.00*tau*freq*dt+ph)*exp2(-0.7*0.007*freq*dt);

        f *= clamp( dt/0.004, 0.0, 0.7 );


        return f;
    }
    else {
        float dt = t-t0;
        float freq = 440. * p * pow(p, float(n));
        float ph = 1.0;

        ph *= sin(6.283185*freq*dt*2.0);
        ph *= 0.5+0.5*max(0.0,5.0-0.01*freq);
        ph *= exp(-dt*freq*0.2);

        float f = 0.;
        f += 0.70*sin(1.00*tau*freq*dt+ph)*exp2(-0.7*0.007*freq*dt);
        f += 0.20*sin(2.01*tau*freq*dt+ph)*exp2(-0.7*0.011*freq*dt);
        f += 0.20*sin(3.01*tau*freq*dt+ph)*exp2(-0.7*0.015*freq*dt);
        f += 0.16*sin(4.01*tau*freq*dt+ph)*exp2(-0.7*0.018*freq*dt);
        f += 0.13*sin(5.01*tau*freq*dt+ph)*exp2(-0.7*0.021*freq*dt);
        f += 0.10*sin(6.01*tau*freq*dt+ph)*exp2(-0.7*0.027*freq*dt);



        f -= 0.35*f*f*f;

        f *= 1.0 + 1.5*exp(-8.0*dt);
        f *= clamp( dt/0.004, 0.0, 0.7 );

        f *= 2.5-1.5*clamp( log2(freq)/10.0,0.0,1.0);

        return f;
    }
}


