#define side(a) dot(uv, vec2(sin(a), cos(a)))
vec3 white = vec3(1.0);
vec3 red = vec3(178., 34., 52.)/255.;
vec3 blue = vec3(60., 59., 110.)/255.;
float pi = 3.14159265;

float star(vec2 uv, float size) {
    uv.x = abs(uv.x);
    float a = pi*2./5.;
    float d1 = side(a);
    a = pi*6./5.;
    float d2 = side(a); 
    a = pi*4./5.;
    float d3 = side(a); 
    float d = min(max(d1, d2), max(uv.y, d3));
    float w = min(.1, fwidth(d));
    return smoothstep(w, -w, d-size); }   

vec2 remap(vec2 lv, float ln, float pv, float pn, float r) {
    return vec2((lv.x-pv)/(r-pv),(lv.y-ln)/(pn-ln)); }

vec3 flag(vec2 uv) {
    float y = sin(uv.y*pi*13.);
    float w = fwidth(y);
    float rw = smoothstep(-w, w, y);
    w = fwidth(uv.y);
    vec3 col = mix(white, red, rw);
    vec2 st = remap(uv, 0.4606, 0.0, 1.0, 0.4);
    float size = 0.085;
    if (st.x > 0. && st.x < 1. && st.y > 0. && st.y < 1.) {
    vec2 gv = fract(st*vec2(6.,5.))-0.5;
    col = mix(mix(col, blue, smoothstep(0.0, w*3., st.y)), white, star(gv, size));  
    st = remap(st, 0.1, 0.0833, 0.9, 0.9166);
    if (st.x > 0. && st.x < 1. && st.y > 0. && st.y < 1.) {
    vec2 gv = fract(st*vec2(5.,4.))-0.5;
    col = mix(col, white, star(gv, size)); } } 
    col *= smoothstep(w, 0.0, abs(uv.y-0.5)-0.5+w);
    return col; }

void mainImage( out vec4 fragColor, in vec2 fragCoord ) {  

    vec2 uv = fragCoord/iResolution.xy;
    float t = uv.x*7.0 - iTime+uv.y*2.;
    uv.y += sin(t)*0.05;
    vec3 col = flag(uv);
    col *= 0.7+cos(t)*0.25;  
    fragColor = vec4(col,1.0); }
