// Resolution
void mainImage(out vec4 fragColor, in vec2 fragCoord)
{
    // Normalized coordinates
    vec2 uv = (fragCoord - iResolution.xy / 2.0) / iResolution.y;

    // Fetch audio data
    int tx = int(mod(fragCoord.x + iTime * 50.0, 512.0));
    float fft  = texelFetch(iChannel0, ivec2(tx, 0), 0).y; // Frequency data
    float wave = texelFetch(iChannel0, ivec2(tx, 1), 0).x; // Sound wave data

    // Time influenced by audio (reduced influence)
    float time = iTime * .1 + fft * 1.0; // Reduced influence of fft

    // Colors influenced by sound
    vec3 c1 = vec3(fft, 0.0, 1.0 - fft); // Color based on frequency data
    vec3 c2 = vec3(1.0 - wave, wave, 0.0); // Color based on wave data

    // Polar coordinates
    float r = length(uv);
    float a = atan(uv.y, uv.x);

    // Spectrum radius influence


    // Geometric patterns influenced by sound
    float p1 = sin(r * 10.0 + time + fft * 5.0) * 2.0;
    float p2 = cos(r * 8.0 + time + wave * 5.0) * 3.0;
    float p3 = sin(r * 6.0 + time + fft * 3.0) * 1.5;
    float p4 = cos(r * 12.0 + time + wave * 3.0) * 2.5;

    // Additional wavy patterns influenced by sound
    float wavePattern1 = sin(a * 10.0 + time * 2.0 + fft * 2.0) * 0.5;
    float wavePattern2 = cos(a * 10.0 + time * 2.0 + wave * 2.0) * 0.5;

    // Combine patterns
    vec3 combined = mix(c1, c2, r);
    combined += p1 * 0.5 + p2 * 0.8 + p3 * 1.2 + p4 * 1.5;
    combined += wavePattern1 * 0.5 + wavePattern2 * 0.5;

    // Alpha blending
    vec3 alpha = vec3(1.0) - vec3(length(p1), length(p2), length(p3)) / 1.0;
    fragColor = vec4(mix(combined, alpha, length(p1)), 1.0);
}
