#define iMAX 140.0
#define REACH 0.001
#define rot(a) mat2(cos(a),sin(a),-sin(a),cos(a))

float hash(float n){return fract(sin(n)*432.123);}

vec4 opElongate( in vec3 p, in vec3 h )
{
    vec3 q = abs(p)-h;
    return vec4( max(q,0.0), min(max(q.x,max(q.y,q.z)),0.0) );
}
float sdTorus( vec3 p, vec2 t )
{
  vec2 q = vec2(length(p.xz)-t.x,p.y);
  return length(q)-t.y;
}
float fold(float a, float b, inout float c){
    float v = mod(a, b)-b/2.;
    c = a-v;
    return v;
}
float MAP(vec3 pos){

    float d;
    float index;
    float cutsize=2.;
    pos.xy*=rot(iTime*0.5);

    pos.z-=iTime*20.;
    float indexY;
    pos.z = fold(pos.z, 4., indexY);
    indexY=floor(indexY)*10.;
    pos.x += (80.*hash(indexY)-40.);
    pos.xy *= rot(443.243*hash(indexY*24.));
    pos.x+=iTime*hash(indexY)*10.;

    vec3 pos2 = pos;
    pos2.x = mod(pos.x, cutsize) - cutsize/2.;
    if(mod(pos.x,cutsize*2.)>cutsize)pos2.yz=pos2.zy;


    {
    vec3 q = pos2 - vec3(-0.9,0.0,.0);
    vec4 w = opElongate( q, vec3(0.4,0.0,0.0) );
    d = w.w+sdTorus( w.xzy, vec2(0.8,0.3) ) ;
    }
    {
    vec3 q = pos2 - vec3(0.9,0.0,.0);
    q.yz=q.zy;
    vec4 w = opElongate( q, vec3(0.4,0.0,0.0) );
    d = min( d, w.w+sdTorus( w.xzy, vec2(0.8,0.3) ) );
    }

    return d;
}
vec3 getNormal(vec3 pos){
    const vec2 e = vec2(0.0001,0.0);

    return normalize(
        vec3(MAP(pos + e.xyy),
             MAP(pos + e.yxy),
             MAP(pos + e.yyx) )-MAP(pos));
}
void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = (fragCoord-iResolution.xy*0.5)/iResolution.y;
    vec3 cameraDir=normalize(vec3(uv,1.0)),
         pos=vec3(0.0,0,-9),
         rayDir=cameraDir;
    float total_d=0.0;
    vec3 color=vec3(.0);
    for(float t_d=0.0,d,i=0.0;i++<iMAX;){
        t_d += d = min(MAP(pos), 1.);
        pos+=rayDir*d;
        if(d<REACH){

            total_d=t_d;
            vec3 amb = vec3(1,0,0);
            if(0.82 > length(abs(cross(getNormal(pos),cameraDir))) )amb*=0.;
            color=0.07 * vec3(1, 1, 1) * total_d*amb;
            //color=getNormal(pos);
            break;

        }
    }

    color=pow(color, vec3(.4545));

    fragColor = vec4(color,1.0);
}
