#define R iResolution.xy
void mainImage(out vec4 O, in vec2 XY) {
    vec2 uv = XY/R;
    vec4 A = texture(iChannel0,uv);
    vec4 B = texture(iChannel1,uv);
    vec4 C = texture(iChannel2,uv);
    O = uv.x<.33?A:(uv.x<.66?B:C);
}

vec3 sky(vec3 dir, float b) {
    // blend blurred version
    return mix(
        texture(iChannel0, dir).rgb,
        texture(iChannel1, dir).rgb,
        b
    );
}

mat3 lookAt(vec3 pos, vec3 focus) {
    mat3 finalMat = mat3(1);

    finalMat[2] = normalize(focus - pos);							// Forward - Z Axis
    finalMat[0] = normalize(cross(finalMat[2], vec3(0, 1, 0))); 	// Right - X Axis
    finalMat[1] = normalize(cross(finalMat[0], finalMat[2])); 		// Up - Y Axis

    return finalMat;
}

void mainImage(out vec4 fragColor, in vec2 fragCoord) {
    vec2 mAng = (iMouse.xy / iResolution.xy) * 6.2832;
    vec2 uvc = (2. * fragCoord - iResolution.xy) / iResolution.y;
    vec2 uv = fragCoord / iResolution.xy;

    vec3 pos = normalize(vec3(cos(mAng.x), cos(mAng.y * .5), sin(mAng.x)));
    vec3 dir = lookAt(pos, vec3(0)) * normalize(vec3(uvc, 1));

    float blend = 2.*pow(abs(.5-uv.y),2.);
    fragColor = vec4(sky(dir,blend), 1);
}

