void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord/iResolution.xy;
    vec2 uvSize = vec2(iResolution.x / iResolution.y, 1.);
    uv *= uvSize;

    float f = 0.;

    // circle

    vec2 c = uvSize/2.;
    // half min screen dimension minus padding
    float r = min(uvSize.x, uvSize.y) / 2. - .1;
    // line
    f += float(abs((distance(uv, c)) - r)  < .003);
    // glow
    f += pow(1.-abs((distance(uv, c)) - r), 60.);


    fragColor = vec4(vec3(f),1.0);
}
