Main {

    R = iResolution.xy;
Q = A(U);
vec4 dQ = vec4(0);
#define K (1./8.)
for (int x = -1; x <= 1; x++)
for (int y = -1; y <= 1; y++)
if (x!=0||y!=0) {
    vec2 v = vec2(x,y);
    vec2 W = U+v;
    vec4 q = A(W);

    vec2 a = vel(Q.xy),
         b = vel(q.xy)+v;
    float ab = dot(v,b-a);
    if (scene(W)) {
        float i = dot(v,(0.5*v-a))/ab;
        float j = .5+.2*clamp(Q.w-q.w,0.05,1.);
        float k = .5+.2*clamp(q.w-Q.w,0.05,1.);
        float wa = K*Q.w*min(i,j)/j;
        float wb = K*q.w*max(k+i-1.,0.)/k;
        dQ += vec4(Q.xyz,1)*wa+vec4(q.xyz,1)*wb;
    } else {
        dQ += K*Q.w*vec4(Q.xyz,1);
    }

}
if (dQ.w>0.) dQ.xyz /= dQ.w;
Q = dQ;
    if (Q.w<0.) Q.w = 0.;
    if (Q.w>10.) Q.w = 10.;
}

Main {
