    vec4 n = A(U+vec2(0,1));
    vec4 e = A(U+vec2(1,0));
    vec4 s = A(U-vec2(0,1));
    vec4 w = A(U-vec2(1,0));
    vec4 m = 0.25*(n+e+s+w);
    if (abs(m.x-Q.x)>.5) Q = m;
    if (scene(U))Q.w = 1.;

    if (iFrame < 1) Q.x = .5*U.x/R.x;
}
