void mainImage(out vec4 fragColor, in vec2 fragCoord) {

    vec2 uv = fragCoord.xy / iResolution.xy * 2.0 - 1.0;
    uv.x *= iResolution.x / iResolution.y; 

    vec3 color = vec3(1.0);

    vec2 start = vec2(-0.8, 0.8); 
    vec2 mid = vec2(0.8, -0.8);  
    vec2 end = vec2(-0.8, -0.8); 

    float totalTime = 6.0;    
    float t = mod(iTime, totalTime) / totalTime; 

    vec2 circlePos;
    if (t < 0.33) {
        float localT = t / 0.33; 
        circlePos = mix(start, mid, localT);
    } else if (t < 0.66) {
        float localT = (t - 0.33) / 0.33; 
        circlePos = mix(mid, end, localT);
    } else { 
        float localT = (t - 0.66) / 0.34;
        circlePos = mix(end, start, localT);
    }

    float circle = smoothstep(0.05, 0.04, length(uv - circlePos)); 


    color = mix(color, vec3(0.0, 0.5, 0.8), circle); 

    fragColor = vec4(color, 1.0);
}
