#define res iResolution
void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = (2.*fragCoord-res.xy)/res.y;


    float a = sin(iTime*0.2+uv.x*0.02+uv.y*0.02);
    uv *= mat2(cos(a),-sin(a),sin(a),cos(a));

    uv *= sin(iTime*0.035+10.0)-0.1;

    vec3 t = texture(iChannel0,vec2(uv)).rgb;
    uv = vec2(uv.x+t.x*0.1,uv.y+t.y*0.1);
    uv.y -= 0.05;


    float c = length(uv);
    float cb,cg = c;
    vec2 ouv = uv;
    uv = uv * vec2(sin(iTime*0.04+4.0),cos(iTime*0.04+4.0));
    c  = c * texture(iChannel0,vec2(uv)).r;
    uv = ouv;
    uv = uv * vec2(sin(iTime*0.02+10.0)*1.1+1.0,cos(iTime*0.02+10.0)*1.1+1.0);
    cb = c * texture(iChannel0,vec2(uv)).b;
    uv = ouv;
    cg = c * texture(iChannel0,vec2(uv)).g;


    vec3 col = vec3(c*0.7,cg*1.2,cb*1.2);

    fragColor = vec4(col,1.0);
}
