void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    float max_desaturation = 0.6;
    vec3 colour = vec3(0.5,1.0,0.0);

    vec2 uv = fragCoord/iResolution.xy;
    float brightness = uv.x;

    float lerp_map = brightness * brightness * max_desaturation;
    vec3 colour_sat_mapped = lerp_map * vec3(1) + (1.0-lerp_map) * colour;
    vec3 colour_mapped = colour_sat_mapped * brightness;

    // Output to screen
    fragColor = vec4(colour_mapped,1.0);
    if (uv.y < 0.5) {
        fragColor = vec4(colour*brightness,1.0);
    }
}
