void mainImage( out vec4 fragColor, in vec2 fragCoord )
{

    vec2 uv = fragCoord.xy/iResolution.xy;

    //size of one texel in iChannel0
    vec2 texel = 1./iResolution.xy;

    vec4 total_color;
    //see 5x5 gaussian weights in Common 
    for (int i=0;i<5;i++){
        float fi = float(i)-2.;
        for (int j=0;j<5;j++){ 
            float fj = float(j)-2.;
            vec4 color = texture(iChannel0, 
                uv + vec2( texel.x*fi,texel.y*fj )  );
            total_color += color * gk1s[i*5 + j];
        }
    }

    fragColor = total_color;
}

vec3  ifs_color, ro;
const float maxDist=9.;



mat2 rot2(float an) {
    float cc=cos(an),ss=sin(an);
    return mat2(cc,-ss,ss,cc);
}

float juliabulb(vec3 p) {

  float power = 1.5 + params[1];

  vec3 z = p;

  float nn = 2.3;
  z = mod(z-nn,2.*nn)-nn;

  z.xy *= rot2(iTime/3. );

  float iv = .8 - .6;
  float tt = mod(iTime ,10.);
  float pp = tt * iv / 5.; 
  if (tt>5.) pp = (10. - tt) *iv/5.;
  vec3 julia = vec3(params[2],params[3]+ .5*sin(iTime), .8 - pp );

  float r, theta, phi=0., dr = 1.0;

  for(float i = 0.; i < params[0]; ++i) {

    r = length(z);

    if(r > 2.0) break;

    theta = atan(z.y, z.x);
    dr = pow(r, power - 1.0) * dr * power + 1.0;
    r = pow(r, power);
    theta = theta * power;
    //phi = phi * power;  //not using phi

    //difference between mandelbulb and juliabulb is the same
    //as regular mbrot vs julia, adding a constant for julia
    z = r * vec3(cos(theta)*cos(phi), sin(theta)*cos(phi), sin(phi)) + julia;

    //colors
    if (z.x*z.y > 0. ) ifs_color.x ++;
    if (z.y*z.z > 0. ) ifs_color.y ++;
    if (z.z*z.x > 0. ) ifs_color.z ++;  
  }

   return 0.5 * log(r) * r / dr ;

}

float scene(vec3 p)
{
  //tunnelling through
  return max( juliabulb(p), -(length(p - ro) - params[13] ) );
}


vec3 ifsColor(vec3 ifs_color) {
  //go ahead and change the vec3 colors here to have some fun
  return ifs_color.x*vec3(1.,.5,0.) + ifs_color.y*vec3(1.,1.,0.) + ifs_color.z*vec3(.2,0.,1.);
}

vec3 render(vec3 ro, vec3 rd)
{
  vec3 col=vec3(0.), normal, pos=ro;

  const int maxSteps = 80;
  float totdist=0., d=0., glow=0.;
  for (int j = 0; j < maxSteps; j ++) {
    d = scene(pos);
    pos += d*rd;
    totdist += d*.7;
    glow += exp(-max(.2-d,0.));
    if (d < ( 3e-4*(1.+totdist*5.*(1.+params[25]))) || totdist > maxDist) break;
  }


  ifs_color = normalize(vec3( ifs_color.x*cos( params[17]*ifs_color.x ),
                              ifs_color.y*cos( params[18]*ifs_color.y ),
                              ifs_color.z*cos( params[19]*ifs_color.z) ) );


  vec3 ifsC = .4*ifsColor(ifs_color);

  col = 4e-1*pow(1.+glow/5.,8.)*ifsC*ifsC*ifsC * exp(-totdist*1.3 );

  return 1.-exp( -clamp (col, 0., 1.) );

}


void mainImage( out vec4 fragColor, in vec2 fragCoord )
{

    vec2 uv = (2.0*fragCoord-iResolution.xy)/iResolution.y*2.;
    ro = vec3(iTime/5.,iTime/7.,-iTime); 
    vec3 rd = normalize( vec3( uv, -5.) );

    rd.yz *= rot2(-.4);
    rd.xz *= rot2(-.4);

    vec3 color =  sqrt( render(ro,rd) );

    fragColor = vec4( (color), 0.);
}
