vec3 palette(float t) {
    vec3 a = vec3(0.5, 0.5, 0.5);
    vec3 b = vec3(0.5, 0.5, 0.5);
    vec3 c = vec3(1.0, 1.0, 1.0);
    vec3 d = vec3(0.263, 0.416, 0.557);

    return a + b * sin(6.28318 * (c * t + d));
}

void mainImage(out vec4 fragColor, in vec2 fragCoord) {
    vec2 uv = (fragCoord / iResolution.xy) * 2.0 - 1.0;
    vec2 uv0 = uv;
    vec3 finalColor = vec3(0.0);

    // Sample the texture from iChannel0 at mipmap level 1.0
    float lodLevel = 1.0; // Adjust this level to see different details
    vec4 texColor = textureLod(iChannel0, uv, lodLevel);

    for (float i = 0.0; i < 3.40; i++) {
        uv = fract(uv * 0.5) - 105.15;

        // Use the texture value to modulate the distance calculation
        float d = length(uv) * exp(length(uv0) + texColor.r);

        vec3 col = palette(length(uv0) * i * 2.4 + iTime * 0.4);

        d = sin(d * 8.0 + iTime * texColor.b / 10.0) / 9.0;
        d = abs(d);

        d = pow(0.01 / d, 1.2);

        finalColor += col * d;
    }

    fragColor = vec4(finalColor, 1.0);
}
