vec2 hash22(vec2 p)
{
	uvec2 q = uvec2(ivec2(p))*uvec2(1597334673U, 3812015801U);
	q = (q.x ^ q.y) * uvec2(1597334673U, 3812015801U);
	return vec2(q) * 2.328306437080797e-10;
}

vec2 value2d(vec2 p)
{
    vec2 pg = floor(p), pc = p-pg;
    vec2 n = vec2(0, 1);

    pc = pc*pc*(3.-2.*pc);

    return mix(
        mix(hash22(pg+n.xx), hash22(pg+n.yx), pc.x),
        mix(hash22(pg+n.xy), hash22(pg+n.yy), pc.x),
        pc.y
    );
}

float sd_lineh(vec2 p, float s){return abs(p.x)-s;}
float sd_linev(vec2 p, float s){return abs(p.y)-s;}

float sd_circle(vec2 p, float s){return length(p)-s;}

void mainImage( out vec4 O, in vec2 U )
{
    vec2 ires = iResolution.xy;
    vec2 uv = (U-.5*ires)/ires.y;
    vec2 m = (iFrame > 0 && dot(iMouse.xy, iMouse.xy) > 0.) 
        ? iMouse.xy 
        : vec2(ires.xy*vec2(.25, .6));

    m = (m - .5*ires)/ires.y;
    vec2 grid = vec2(10.);
    vec2 uv_grid = uv*grid;
    vec2 speed = vec2(34.);

    vec2 t = floor(mod(iTime,3.)*.45*speed);
    vec2 uv_n0 = uv + value2d((uv_grid)+t)*.01;
    vec2 uv_n1 = uv + value2d((uv_grid+grid)+t)*.01;
    vec2 uv_n2 = uv + value2d((uv_grid+2.*grid)+t)*.01;

    vec2 th = .01/ires;
    vec2 sp = 3./ires;

    float lines = 
    min(min(
        min(sd_lineh(uv_n0-m, th.x), sd_linev(uv_n0-m, th.y)),
        min(sd_lineh(uv_n1-m+sp, th.x), sd_linev(uv_n1-m+sp, th.y))
    ),  min(sd_lineh(uv_n2-m-sp, th.x), sd_linev(uv_n2-m-sp, th.y)));

    O=vec4(smoothstep(.002, .001, lines));
}
