const float threshold = 20.;
const float quiltColumns = 8.;
const float quiltRows =    5.;

vec2 mirrored(vec2 v) {
  vec2 m = mod(v,2.);
  return mix(m,2.0 - m, step(1.0 ,m));
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 coord = (fragCoord.xy / iResolution.xy ) * vec2(quiltColumns, quiltRows);
    vec2 fractCoord = fract(coord);
    vec2 floorCoord = floor(coord);
    float imageId = floorCoord.x + floorCoord.y * quiltColumns;
    float valueId = imageId / (quiltColumns * quiltRows - 1.);
    vec2 uv = fractCoord;
    vec4 depthMap = texture(iChannel1, mirrored(uv));
    vec2 fake3d = vec2(uv.x + (depthMap.r - 0.5) * (( valueId - 0.5 ) * 2. / threshold), uv.y );
    fragColor = texture(iChannel0, mirrored(fake3d));

    // debug 边线和将红色图转为白色
        if(uv.x < 0.01 || uv.x > 9.9 || uv.y < 0.01 || uv.y > 9.9) {
           fragColor = vec4(valueId, 1., 1., 1.);
        }
        fragColor = vec4(fragColor.r, fragColor.r, fragColor.r, 1.0);
}
