void mainImage(out vec4 fragColor, in vec2 fragCoord) {

       vec2 uv = fragCoord / iResolution.xy * 2.0 - 1.0;
         //uv *= vec2(iResolution.x / iResolution.y, 1.0);  //square 

     vec2 freq = vec2(1.5,6.); // freq


   if(iMouse.x == 0.||iMouse.y == 0.){
          freq = vec2(10. * cos(iTime), 7.5 * sin(iTime)); // freq animated

    }
   else{
          freq = vec2(iMouse.x/50.,iMouse.y/50.); // freq iMouse
    }


 float pattern = sin(freq.x * uv.x) * cos(freq.y * uv.y); // waves
      pattern += cos(freq.y * uv.x) * sin(freq.x * uv.y); // waves

    vec3 color = abs (vec3(0.5 * pattern, 0.5 * pattern, 0.0 - pattern)); // color
     fragColor = vec4(color, 1.0);
}
