#define go void mainImage( out vec4 O, in vec2 I )
#define R iResolution
#define C0 iChannel0
#define C1 iChannel1
#define tex(x,y) texelFetch(x,y,0)

vec3 hsv( in vec3 c ){
    vec3 rgb = clamp( abs(mod(c.x*6.0+vec3(0.0,4.0,2.0),6.0)-3.0)-1.0, 0.0, 1.0 );
	rgb = rgb*rgb*(3.0-2.0*rgb);
	return c.z * mix( vec3(1.0), rgb, c.y);}
//smooth hsv from iq https://www.shadertoy.com/view/MsS3Wc

go{
O=vec4(0.);
if(iFrame<3){O.x=tex(C1,ivec2(I*(450./R.y))).x;return;}
vec4 me= tex(C0,ivec2(I));
O.z=me.x;
for(float i=-1.;i<=1.;i++){
for(float j=-1.;j<=1.;j++){
if(i==0.&&j==0.){O.x+=.2*me.x;continue;}
if(abs(i)==abs(j)){O.x-=.1*tex(C0,ivec2(mod(I.x+i,R.x),mod(I.y+j,R.y))).x;continue;}
O.x+=.27*tex(C0,ivec2(mod(I.x+i,R.x),mod(I.y+j,R.y))).x;
}}
O.x=round(abs(sin(O.x)))-me.z;
if(iFrame%300==0){O=vec4(O.z,0.,O.x,0.);}
O.y=-O.z;
}
