#define ITER 40.

void mainImage( out vec4 O, in vec2 I )
{
    vec2 p = (2.*I - iResolution.xy) / iResolution.y;
    p.y -= .4;

    vec3 c; float f,w = 0.; vec2 fp = vec2(0.);
    for(float i = 0.; i++ < ITER;) {        

        fp = 20. * p*i*.004/(p.y+2.);
        fp.y -= iTime*.5;

        mat2 m = mat2( 1.6,  1.2, -1.2,  1.6 );
        f  = 0.5000*noise( fp ); fp = m*fp*1.1;
		f += 0.2500*noise( fp ); fp = m*fp;
        f += 0.1250*noise( fp ); fp = m*fp;
        f = smoothstep(-.04,.7,f);
        w += f;

    }
    w /= ITER*.75;

    vec3 col = mix(
        vec3(smoothstep(-.11,1.2,w)),
        vec3(6.,2.,.5), smoothstep(.05,1.,w));  

    vec2 uv = I/iResolution.xy;
    vec2 n = uv*(1. - uv) * 6.;
    col *= pow(n.x*n.y,.5) + .5;

    O = vec4(aces_tonemap(col*2.),0);
}

// https://www.shadertoy.com/view/XdXGW8
vec2 grad( ivec2 z )  // replace this anything that returns a random vector
{
    // 2D to 1D  (feel free to replace by some other)
    int n = z.x+z.y*11111;

    // Hugo Elias hash (feel free to replace by another one)
    n = (n<<13)^n;
    n = (n*(n*n*15731+789221)+1376312589)>>16;

#if 0

    // simple random vectors
    return vec2(cos(float(n)),sin(float(n)));

#else

    // Perlin style vectors
    n &= 7;
    vec2 gr = vec2(n&1,n>>1)*2.0-1.0;
    return ( n>=6 ) ? vec2(0.0,gr.x) : 
           ( n>=4 ) ? vec2(gr.x,0.0) :
                              gr;
#endif                              
}

float noise( in vec2 p )
{
    ivec2 i = ivec2(floor( p ));
     vec2 f =       fract( p );

	vec2 u = f*f*(3.0-2.0*f); // feel free to replace by a quintic smoothstep instead

    return mix( mix( dot( grad( i+ivec2(0,0) ), f-vec2(0.0,0.0) ), 
                     dot( grad( i+ivec2(1,0) ), f-vec2(1.0,0.0) ), u.x),
                mix( dot( grad( i+ivec2(0,1) ), f-vec2(0.0,1.0) ), 
                     dot( grad( i+ivec2(1,1) ), f-vec2(1.0,1.0) ), u.x), u.y);
}


// https://www.shadertoy.com/view/4djSRW
vec2 hash22(vec2 p)
{
	vec3 p3 = fract(vec3(p.xyx) * vec3(.1031, .1030, .0973));
    p3 += dot(p3, p3.yzx+33.33);
    return fract((p3.xx+p3.yz)*p3.zy);

}

vec3 aces_tonemap(vec3 color)
{	
	mat3 m1 = mat3(
        0.59719, 0.07600, 0.02840,
        0.35458, 0.90834, 0.13383,
        0.04823, 0.01566, 0.83777
	);
	mat3 m2 = mat3(
        1.60475, -0.10208, -0.00327,
        -0.53108,  1.10813, -0.07276,
        -0.07367, -0.00605,  1.07602
	);
	vec3 v = m1 * color;    
	vec3 a = v * (v + 0.0245786) - 0.000090537;
	vec3 b = v * (0.983729 * v + 0.4329510) + 0.238081;
	return pow(clamp(m2 * (a / b), 0.0, 1.0), vec3(1.0 / 2.2));	
}
