#define PI 3.14159265359
mat2 rotate2d(float _angle) {
  return mat2(cos(_angle), -sin(_angle),
    sin(_angle), cos(_angle));
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
vec2 uv = gl_FragCoord.xy / iResolution.xy;
  uv -= vec2(0.5);
  uv = rotate2d(sin(iTime) * PI) * uv;
  uv += vec2(0.5);
  float d0 = distance(uv, vec2(0.5));
  // 大圆
  float r0 = step(d0, .25);
  // 圆框
  float d1 = step(d0, 0.16);
  float d2 = step(.15, d0);
  // 小圆
  float r1 = step(.05, d0);
  r0 = min(r0, r1);
  r0 = min(r0, 1. - min(d2, d1));
  // 上小圆
  float d3 = distance(uv, vec2(0.5, 0.655));
  float r2 = step(d3, .04);
  r0 = min(r0, 1. - r2);
  float r4 = radians(60.);
  float r5 = radians(30.);
  // 右下小圆
  float d4 = distance(uv, vec2(0.5 - .155 * sin(r4),
      0.345 + .155 * sin(r5)));
  float r3 = step(d4, .04);
  r0 = min(r0, 1. - r3);
  // 左下小圆
  float d5 = distance(uv, vec2(0.5 + .155 * sin(r4),
      0.345 + .155 * sin(r5)));
  float r6 = step(d5, .04);
  r0 = min(r0, 1. - r6);

    fragColor =vec4(r0, 0.0, 0.0, 1.0);
}
