#define t iTime*.2

void mainImage( out vec4 C, vec2 U )
{
    vec2 R = iResolution.xy, z = ( U+U - R ) / R.y;
    z = vec2(log(length(z)), atan(z.x,z.y) + t);

    // this makes 5-star shape
    float s = .6*abs(sin(z.y*2.5));

    z.x = fract(z.x + z.y/6.28 + s - t);

    C = texture(iChannel0, exp(z.x-s) * vec2(-sin(z.y-t)*.6, cos(z.y-t))/5.+.5) + step(z.x, .1);
}
