#define PI 3.1416

float sawtooth(float x, float range) {
    return (x - floor(x)) * range;
}

float sdBox(vec2 pos, vec2 origin, vec2 dim, float r) {
    vec2 cPos = abs(pos - origin);
    vec2 dist = cPos - (dim - vec2(r)) * 0.5;

    float d = 0.0;
    if (dist.x > 0.0 && dist.y > 0.0) d = length(dist);
    else d = max(dist.x, dist.y);
    return d - r * 0.5;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord ) {
    vec2 uv = fragCoord/iResolution.xy;
    uv -= 0.5;
    uv.x *= iResolution.x / iResolution.y;

    float fac = 0.5 * sin(iTime) + 0.5;
    int offset = int(floor(iTime / 3.0)) % 3;

    vec3 color = vec3(1.0);
    float dist = sdBox(uv, vec2(0.0, 0.0), vec2(1.0, 0.5), sin(sawtooth(iTime * 0.25, PI)) * 0.5);
    color = mix(vec3(1.0, 1.0, 1.0), vec3(0.25, 0.6, 1.0), sin(fract(dist) * 175.0));
    color *= mix(vec3(1.0, 0.5, 0.4), vec3(1.0), smoothstep(0.0, 0.003, dist));
    color *= smoothstep(0.0, 0.02, abs(dist * 0.8));


    fragColor = vec4(color, 1.0);
}
