vec3 star(vec2 uv, vec2 coord) {
    // Example logic to create a star effect
    float brightness = smoothstep(0.0, 0.1, 1.0 - length(uv - coord));
    return vec3(brightness);
}

vec3 gradient(vec2 uv) {
    return 0.5 + 0.5 * cos(iTime + uv.xyx + vec3(0.0, 2.0, 4.0));
}

void mainImage(out vec4 fragColor, in vec2 fragCoord) {
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord / iResolution.xy;

    // Combine star and gradient effects
    vec3 color = star(uv, vec2(0.5, 0.5)) + gradient(uv);

    // Output to screen
    fragColor = vec4(color, 1.0);
}
