void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec4 Aberration = vec4(.2f, .1f, 0.0f, 1.f);
    float Blur = 0.1f;
    float Radial = .3f;

    vec2 uv = fragCoord / iResolution.xy;

    float mask = min(length(uv - vec2(.5f)) * 2.f + .5f, 1.f);
    float tex1 = texture(iChannel0, uv).y;
    tex1 *= mix(1.f, Radial, mask);
    vec3 tex2 = texture(iChannel0, uv - (uv - vec2(.5f)) * Blur).xyz;
    vec3 tex3 = texture(iChannel0, uv + (uv - vec2(.5f)) * Blur).xyz;
    vec3 col = vec3(tex2.x * Aberration.y + tex3.x * Aberration.x,
                    tex2.y * Aberration.z + tex3.y * Aberration.z + tex1 * Aberration.w,
                    tex2.z * Aberration.x + tex3.z * Aberration.y);
    fragColor = vec4(col, 1.f);

}
