void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord/iResolution.xy;

    vec2 delta = vec2(iMouse) - fragCoord;


    const float DURATION = 3.0;
    float t = mod(iTime, DURATION) / DURATION;
    float fade_out = smoothstep(t, 1.0, 0.95);
    float ring = t * 1000.0;

    float dist = pow(ring + length(delta), 0.55);
    float ring_dist = mod(dist, 150.0);


    const float THICKNESS = 1.3;
    float thickness = THICKNESS * pow(length(delta)*0.005, 0.4);


    const float FALLOFF = 20.0;
    float in_ring = smoothstep(length(delta)-FALLOFF, length(delta)+FALLOFF, ring);


    float ring_color = cos(0.5 * 3.14159 * abs(THICKNESS - ring_dist) / THICKNESS);

    float wave = ring_color * in_ring * fade_out;


    fragColor = texture(iChannel0, uv+normalize(delta)*wave*0.005);
    fragColor.xyz += 0.025*wave;

}
