// Parameter Setting
// The greater, the vaguer. 0 means no pixelation.
#define VAGUE_LEVEL 3.

#define SEED 1514.
#define IMG_WIDTH 512.
#define IMG_HEIGHT 512.
////////////////////

const float VAGUE = pow(2., VAGUE_LEVEL);
const float X_UNIT = 1./ IMG_WIDTH * VAGUE;
const float Y_UNIT = 1./ IMG_HEIGHT * VAGUE;

float band(float value, float min, float max) {
    return step(min,value) - step(max, value);
}

// Return ivec2 less than para:Mvec

ivec2 rand(float seed, float max) {
    float x = fract(sin(seed * 12.9898 + 78.233) * 43758.5453);
    float y = fract(cos(seed * 15.2348 + 61.263) * 56895.1425);
    return ivec2(x,y) * int(max);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord ) {

    vec2 uv = (fragCoord  - iResolution.xy *  .5) / iResolution.y * 1.;
    uv += 0.5;
    vec3 col = vec3(0.);
    if ((band(uv.x,0.,1.) - 0.)<0.5) {
        // 非图像区域保证一直是黑色
        col -= 1.;
    }

    int xInt = int(uv.x / X_UNIT);
    int yInt = int(uv.y / Y_UNIT);

    fragColor = texelFetch(iChannel0, int(VAGUE)*ivec2(xInt,yInt)+rand(SEED,VAGUE),0);


}
