#ifdef GL_ES
precision mediump float;
#endif

void mainImage(out vec4 fragColor, in vec2 fragCoord) {
    vec2 uv = fragCoord / iResolution.xy;
    float alpha = 0.0;

    for (float i = 0.0; i < 50.0; i++) {
        float dropX = fract(sin(i * 12.9898) * 43758.5453);
        float dropY = fract(sin(i * 78.233) * 43758.5453);
        float rainSpeed = -0.5 * (0.5 + fract(sin(i * 34.789) * 12345.678) * 0.5);
        float dropSize = 0.000001 * (0.5 + fract(sin(i * 56.789) * 98765.432) * 0.5);
        vec2 rainDropPos = vec2(dropX, fract(dropY + iTime * rainSpeed));
        vec2 stretchedUV = vec2(uv.x, uv.y * (0.02 + fract(sin(i * 45.678) * 65432.1) * 0.01));
        alpha += exp(-length(stretchedUV - rainDropPos) * length(stretchedUV - rainDropPos) / dropSize);
    }

    fragColor = vec4(vec3(2.0) * alpha, alpha);
}
