#define h11(a) fract(sin(a * 453.2734)* 994.3434) 
float cor;

float sBox(vec2 p){
    float k = 6.;
    p = pow(abs(p), vec2(k));
    return pow(dot(p, p/p), 1./k) - .515;
}

vec2 rain(vec2 p){
    float h = 14., id = floor(p.x),
          t = iTime + h11(id) * 1.3;

    p.x = fract(p.x) - .5;
    p.y += sin(id + t) + fract(t) + 1.;
    cor = (p.y < .0 ? floor(p.y): 0.) + floor(t);
    p.y < .0 ? h = 1. : h;
    p.y += (h / 2. - .5) * fract(t);
    p.y = mod(p.y, h) - h / 2.;

    return p;
}


void mainImage(out vec4 o, vec2 u) {
    vec2 p, r = iResolution.xy; 
         u = 3. * vec2(u + u - r) / r.y;

    float i, d, t = iTime;

    p = rain(u);
    d = sBox(p);
    d = max(max(d, -u.x - 5.), u.x - 5.) + .03;

    cor = mod(cor, 3.);
    vec4 col = cor == 1. ? vec4(1, .6, 0, 0) : cor == 2. ? vec4(1) : vec4(0, .6, 1, 0);

    o = smoothstep(10./r.y, .0, d) * (col - length(p - .3) / 1.8);
}
