#define R iResolution.xy
#define Main void mainImage(out vec4 Q, in vec2 U) 
#define A(U) texture(iChannel0,(U)/R)
#define B(U) texture(iChannel1,(U)/R)
vec4 hash44(vec4 p4)
{
	p4 = fract(p4  * vec4(.1031, .1030, .0973, .1099));
    p4 += dot(p4, p4.wzxy+33.33);
    return fract((p4.xxyz+p4.yzzw)*p4.zywx);
}
#define ei(a) mat2(cos(a),sin(a),-sin(a),cos(a))
#define pi 3.14159265359
#define phi 1.618

Main {
    Q = A(U);
    vec2 V = 2.*(U-.5*R)/R.y;
    V.xy *= vec2(.8,1.2);
    for (float i = 0.; i < 100.; i++) {
        vec3 W = vec3(V,0);
        float c = 1.;
        for (float j = 0.; j < 4.; j++) {
            vec4 h = hash44(vec4(i+j*100.,U,iFrame));
            float N = floor(64.);
            float n = floor(h.z*N)/N;
            float b = mod(floor(n*N),3.);
            float a = 2.*pi*n*(1.+b)/2.;
            W *= 1.8;
            W.xy *= ei(a);

            W.x += 2.*n*exp2(-j);
            c += 6.*n/(j+1.);

            vec4 dQ= exp2(4.*j)*exp(-80.*length(W.xy))*max(sin(-2.+c+vec4(1,2,3,100)),0.);
            Q.xyz += .5*dQ.xyz;
        }
    }



}

