bool in_circle(vec2 p, vec2 center, float radius) {
    if(length(center - p) < radius) {
    	return true;   
    }
    return false;
}

void mainImage(out vec4 fragColor, in vec2 fragCoord) {
    vec2 resolution = iResolution.xy;
    float aspect = resolution.x/resolution.y;
    vec2 uv = (2.0*fragCoord-resolution)/resolution.y;

    vec2 circle_center = vec2(0.0, 0.0);
    vec4 base_color = vec4(1.0, 1.0, 1.0, 1.0);
    vec4 circle_color = vec4(1.0, 0.0, 0.0, 1.0);
    vec4 result_color = base_color;
    float circle_radius = 1.0;

    if(in_circle(uv, circle_center, circle_radius)) {
		result_color = circle_color;
    }

    fragColor = result_color;
}
