const float pi = 3.14159265359;

float p_z;                // pixel_size
const float w_n = 4.0;    // sqrt of waves_numbers
float t;                  // how much white here

float w(vec2 p,float t){
    return ceil( (cos(p.x*pi*w_n)+cos(p.y*pi*w_n) +4.0*(t-0.5))/4.0 );    // calculate the wave paterne in x and y and 'ceil' it
}

void mainImage( out vec4 fragColor, in vec2 fragCoord ){
    vec2 uv = (fragCoord*2.0-iResolution.xy)/iResolution.y;    // to have diferent pixels


    vec2 mouse = iMouse.xy/iResolution.xy;

    // here put your code for calculating 'p_z' and 'w_n'
    if(iMouse.w != 0.0){
        p_z = iResolution.y*mouse.x;
    }
    else {
        p_z = iResolution.y/4.0;
    }
    //

    uv = round(uv*p_z)/p_z;

    // here put your code for calculating 't'
    if(iMouse.w != 0.0){
        t = sin(iTime*pi/2.0)/2.0+0.5;
    }
    else {
        t = sin(iTime*pi/2.0)/2.0+0.5;
    }
    //

    fragColor = vec4(w(uv,t));    // you can modify your color insted of black and white
}
