float sumV(vec2 v) {
    return v.x+v.y;
}

void mainImage(out vec4 fragColor, in vec2 fragCoord) {
    vec2 uv0 = fragCoord.xy / iResolution.xy;
    vec2 uv = fragCoord.xy / iResolution.xy;
    uv = uv * 2.0 - 1.0;
    uv.x *= iResolution.x / iResolution.y;

    float tsc = 3.; // timescale-- scroll dots faster
    vec2 uvScale = vec2(4.,2.);

    vec3 col = vec3(0.);

    float line = abs(sin(sumV(uv*uvScale) + iTime*tsc));
    float depth = mix(0.15,0.25, line);

    float circ = smoothstep(0., depth, length(fract(uv * 15.0) - 0.5));

    vec4 tex = 1.-texture(iChannel0, uv0);

    if (step(1.,circ)>0.5) {
        col.g = .75;
    } else if (step(0.8,circ)>0.5) {
        col.r = 0.75 + round(tex.r)*0.25;
    } else if (step(0.5,circ)>0.5) {
        col.b = 0.75 + round(tex.b)*0.25;
    }

    //col = vec3(line);
    //col = vec3(depth);

    fragColor = vec4(col, 1.0);
}
