float random(float seed) {
    return fract(sin(seed * 5.0) * 43758.5453);
}

void mainImage(out vec4 fragColor, in vec2 fragCoord)
{
    vec2 uv = fragCoord / iResolution.xy;

    float squareSize = 0.1;

    fragColor = vec4(0.0, 0.0, 0.0, 1.0);

    for (int i = 0; i < 10; i++)
    {
        // Generate a random seed for each square
        float seed = float(i) * 10.0;

        // Calculate the moving center of the square
        float moveSpeed = 0.2 + random(seed) * 0.6;
        // Moving the U coordinate from -1 to 1
        float uPos = fract(iTime * moveSpeed + random(seed) * 10.0) * 2.0 - 1.0; 

        // Generate a random V coordinate for each square
        float vPos = random(seed + 100.0);

        // Calculate the width of the square based on its U position
        float uStretchFactor = 1.0 + (1.0 - abs(uPos));

        // Stretched size of the square
        vec2 squareSizeVec = vec2(squareSize * uStretchFactor, squareSize + .1);
        vec2 squareCenter = vec2(uPos, vPos);

        // Calculate the distance from the current fragment to the center of the square
        vec2 dist = abs(uv - squareCenter);

        // locate square
        bool insideSquare = all(lessThan(dist, squareSizeVec * 0.5));

        // Calculate the fade effect based on time
        float fade = 1.0 - abs(uPos);

        // Apply the fade effect
        vec4 squareColor = vec4(fade, fade, fade, 1.0);

        // Colorize squares only
        if (insideSquare) {
            fragColor = squareColor;
        }
    }
}
