float distanceSqrt(vec2 p1, vec2 p2) {
    vec2 d = p1 - p2;
    return dot(d, d); // Squared distance
}

float circle(vec2 uv, vec2 center) {
    return clamp(pow(1. - distanceSqrt(uv, center), 125.), 0., 1.);
}

