#define ROT(a)      mat2(cos(a), sin(a), -sin(a), cos(a))

float bwp(vec2 p, in vec3 F) {
  p = sin(p*2.0*3.14)*0.5+0.5 - F.x;
  float s = sign(p.x * p.y);
  p = abs(abs(p) - F.y);
  return s * (max(p.x, p.y) - F.z);
}

void mainImage( out vec4 C, in vec2 fragCoord ) {
  vec2 p = ( 2.* fragCoord - iResolution.xy )  / iResolution.y;
  float x = p.x;
  p *= ROT(0.2 * iTime);

  float s = 2.0;
  float aa = s * 2.0 / iResolution.y; 

  int i = int(mod(iTime-1.0, 7.0));
  vec3 F =  i==0 ? vec3(1  ,.25, .25)
          : i==1 ? vec3(1.4,.25, .25)
          : i==2 ? vec3(1  ,.2 , .7 )
          : i==3 ? vec3(.75,.5 , .25)
          : i==4 ? vec3(.75,.25, .5 )
          : i==5 ? vec3(.5 ,.5 , .25)
          :        vec3(.5 ,.25, .25);
  float d = bwp(p * s, F);
  if(x < 0.0) d = -d;
  C = vec4(smoothstep(aa, -aa, d));
}
