// ported from https://x.com/FreyaHolmer/status/1822651337642738070

const int dBits[5] = int[5](
  3959160828,
  2828738996,
  2881485308,
  2853333412,
  3958634981
);

float DrawDigit(ivec2 px, const int digit) {
  if (px.x < 0 || px.x > 2 || px.y < 0 || px.y > 4)
    return 0.0; // pixel out of bounds
  int xId = (digit == -1) ? 18 : 31 - (3 * digit + px.x);
  return float((dBits[px.y] & (1 << xId)) != 0);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord ) {
  vec2 uv = fragCoord / iResolution.xy;
  uv = (uv-0.5) * vec2(1, iResolution.y/iResolution.x) + 0.5;
  float c = 0.0;
  for (int i = 0; i < 5; i++) {
    int n = int(floor(iTime / pow(10.0, float(i - 2)))) % 10;
    vec2 st = (vec2(uv.x + float(i) * 0.1 - 0.18, 1.03 - uv.y) - 0.5) * 50. + 0.5;
    c = max(c, DrawDigit(ivec2(floor(st)), n));
  }
  fragColor = vec4(c, c, c, 1);
}
