void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord/iResolution.xy;

    // Breathing parameters
    float breathingSpeed = 0.5*speedFactor; 
    float breathingAmplitude = 0.005;
    float distortionScale = 15.0; // Controls the scale of the distortion pattern

    // Breathing factor
    float breathingFactor = sin(iTime * breathingSpeed) * breathingAmplitude;

    // Create a distortion vector field
    vec2 distortion = amplitudeFactor*vec2(1.,iResolution.x/iResolution.y)*breathingFactor * sin(uv * distortionScale + iTime*breathingSpeed); 

    // Clamp UVs to prevent out-of-bounds access
    vec2 morphedUV = clamp(uv - distortion, 0.0, 1.0); 

    // Sample from iChannel0 

    vec3 color = texture(iChannel0, morphedUV).rgb;
    if(type==1){
        color = texture(iChannel1, morphedUV).rgb;
    }
    float c = smoothstep(0.5,0.2,length(morphedUV-0.5));
//color = vec3(1.)*c;
    fragColor = vec4(color, 1.0);
}

int type = 1;
float amplitudeFactor = 1.;
float speedFactor = 0.5;
