// 217 chars
float P(vec2 p) {
    vec2 f = floor(p),
        q = p - f,
        i = (3. - q - q) * q * q;
    ivec4 r = ivec4(f, ++f);
    vec4 h = vec4((r.xzxz * 959093 ^ r.yyww * 920393) % 3183),
        o = vec4(q, --q),
        d = sin(h) * o.xzxz + cos(h) * o.yyww;
    return mix(mix(d.x, d.y, i.x), mix(d.z, d.w, i.x), i.y);
}

void mainImage(out vec4 c, in vec2 u) {
    c = vec4(vec3(P(u * .05)*.5+.5),1.);
}
