const vec2 iCenter = vec2(0.5, 0.5);
const float iFeather = 0.3;
const float iAngle = 90.0;


vec4 linearMask(sampler2D tex, vec2 ouv, vec2 uv, float angle, float feather, vec2 center) {
    float theta = radians(angle);
    vec2 direction = vec2(sin(theta), cos(theta));
    vec2 u_direction = normalize(direction);
    vec2 u_uv = normalize(uv);
    float alpha = dot(u_direction, u_uv) * length(uv);
    alpha = smoothstep(-0.005 - feather, 0.005 + feather, alpha);
	alpha = clamp(alpha, 0.0, 1.0);


    vec4 origin = texture(tex, ouv);
    origin *= alpha;
    return origin;
}


void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 ouv = fragCoord/iResolution.xy;
    vec2 center = iCenter;
    vec2 uv = ouv - center;
    uv.x *= iResolution.x / iResolution.y;


    vec4 leftColor = linearMask(iChannel0, ouv, uv, 90.0, iFeather, center);
    vec4 rightColor = linearMask(iChannel1, ouv, uv, 270.0, iFeather, center);

    fragColor = leftColor + rightColor;
}
