void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    float time = iTime * 15.0;
    float r = (1.3 + cos((time * 6.0 + fragCoord.x * 0.3) * 0.014) + sin((time * 3.0 + (fragCoord.x * 0.5 - fragCoord.y) * 1.2) * 0.0142857142857143) * 0.5) * 0.6;
    float g = (0.6 + cos(r * 2.0 +
                         (time * 4.0 + (fragCoord.y) * 0.5) * 0.02) + sin((time * 10.0 + (fragCoord.x + fragCoord.y) * 0.9) * 0.01) * 0.5);

    float sum = (r * 1.4 + g - 0.25) * 0.5;
    float b = 1.0 - sum;

    fragColor = vec4(r * 1.05, g, b, 1.0);
}
