float maskGreen(vec3 color)
{
    // Ajusta para incluir uma margem de tolerância ao redor do verde
    float threshold = 0.12; // Aumenta ou diminui para ajustar a tolerância
    float greenIntensity = color.g - max(color.r, color.b);

    // Utiliza a intensidade do verde ajustada para calcular a máscara
    return clamp(greenIntensity / threshold, 0.0, 1.0);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normaliza as coordenadas do fragmento
    vec2 uv = fragCoord / iResolution.xy;

    // Carrega as texturas
    vec3 videoColor = texture(iChannel0, uv).rgb;
    vec3 backgroundImage = texture(iChannel1, uv).rgb;

    // Calcula a máscara baseada na cor verde
    float mask = maskGreen(videoColor);

    // Suaviza a máscara
    float smoothMask = smoothstep(0.2, 0.6, mask);

    // Mistura o vídeo e o fundo com base na máscara
    vec3 finalColor = mix(videoColor, backgroundImage, smoothMask);

    // Define a cor de saída
    fragColor = vec4(finalColor, 1.0);
}
