#ifndef PI
#define PI 3.1415926535897932384626433832795
#endif

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{

    vec2 st=(fragCoord.xy - iResolution.xy / 2.) / iResolution.x;

    vec2 st_center=st;

    vec3 color=vec3(0.);

    float angle=atan(st_center.y,st_center.x)/PI;

    float dist=dot(st_center,st_center)*4.;

    float radius=0.;

    radius = .3 * cos(angle*PI*03. + sin(iTime)*3.);

    float line_width = .02;
    float outer_line_width_coef = 10.;

    float mask=
        smoothstep(radius-line_width,radius,dist)-
        smoothstep(radius,radius+line_width*outer_line_width_coef,dist);

    color+=vec3(1.,.0,.0)*mask;

    fragColor=vec4(color,1.);

}
