vec2 CenterUV(vec2 fragCoord )//uv的居中与反拉伸https://www.shadertoy.com/view/M3cSDS
{
    return (2.0 * fragCoord - iResolution.xy) / min(iResolution.x, iResolution.y);
}

vec4 TextureCut(sampler2D tex,vec2 uv)//图片裁切留下0-1部分https://www.shadertoy.com/view/XcXczr
{
    vec4 color = vec4(0.);
    if(uv.x>0.&&uv.x<1.&&uv.y>0.&&uv.y<1.)//去掉重复
        color = texture(tex,uv);
    return color;
}

vec4 CoverTex(vec4 downColor,vec4 upColor )//图案覆盖https://www.shadertoy.com/view/XcXczr
{
    vec3 color = downColor.rgb + upColor.rgb ;//非重合区域的颜色是各自的颜色
    return vec4(mix(color,upColor.rgb,upColor.a),min(1.,downColor.a+upColor.a));//重合区域的颜色为upcolor,alpha叠加
}


void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = CenterUV(fragCoord );
    vec4 color = vec4(texture(iChannel0,uv).rrr*0.5,0.5);
    color = CoverTex(color,TextureCut(iChannel1,uv+0.5));

    fragColor = color;
}
