void mainImage(out vec4 O, in vec2 I)
{
    vec2 uv = 1.69 * iResolution.z * (2. * I - iResolution.xy) / iResolution.y;
    uv.x += 1.;
    float val = 0.5;
    float dist = 100.;
    for(uint i; i < 100u; i++)
    {
        val = uv.x * val * (1. - val);
        if(i > 50u)
        {
            dist = min(dist, abs(uv.y - val));
        }

    }
    vec3 col = vec3(0.005 / dist);
    O = vec4(col,1.0);
}
