void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    /* Chad
    if (fragCoord.x < iResolution.x/3.0) // Left
    {
        fragColor = vec4(55.0, 75.0, 161.0, 255.0)/255.0;
    }
    else if (fragCoord.x > iResolution.x/1.5) // Right
    {
        fragColor = vec4(237.0,27.0,36.0,255.0)/255.0;
    }
    else // Middle
    {
        fragColor = vec4(242.0,236.0,38.0,255.0)/255.0;
    }
    */

    /* Guinea
    if (fragCoord.x < iResolution.x/3.0) // Left
    {
        fragColor = vec4(208.0, 32.0, 42.0, 255.0)/255.0;
    }
    else if (fragCoord.x > iResolution.x/1.5) // Right
    {
        fragColor = vec4(13.0,150.0,98.0,255.0)/255.0;
    }
    else // Middle
    {
        fragColor = vec4(250.0,209.0,19.0,255.0)/255.0;
    }
    */

    /* Mauritius
    if (fragCoord.y < iResolution.y/4.0) // 4
    {
        fragColor = vec4(20.0, 149.0, 82.0, 255.0)/255.0;
    }
    else if (fragCoord.y < iResolution.y/2.0) // 3
    {
        fragColor = vec4(247.0,236.0,22.0,255.0)/255.0;
    }
    else if (fragCoord.y < iResolution.y/1.33) // 2
    {
        fragColor = vec4(38.0,56.0,128.0,255.0)/255.0;
    }
    else // 1
    {
        fragColor = vec4(222.0,31.0,39.0,255.0)/255.0;
    }
    */

    // ------------------------------------------------------------- Japan

    vec2 uv = fragCoord / iResolution.xy;

    float aspect = iResolution.y / iResolution.x;

    uv = uv -  vec2(0.5, 0.5);

    uv = uv * vec2(1, aspect);

    if(length(uv) < 0.1)
    {
        fragColor = vec4(1, 0, 0, 0);
    }
    else
    {
        fragColor = vec4(1);
    }

    // -------------------------------------------------------------------------- Belgium Code

    float u_angle = (sin(iTime * 0.5) - 33.0) / 10.0;
    float sin_factor = sin(u_angle);
    float cos_factor = cos(u_angle);
    vec2 coord = fragCoord / iResolution.xy;
    vec2 uvN = vec2(coord.x - (0.6 * iResolution.x / iResolution.y), coord.y - 0.9) * mat2(cos_factor, sin_factor, -sin_factor, cos_factor);


    float fs = sin(iTime - uvN.y * 4.0) * 3.0;
    float fs2 = sin(iTime - (uvN.y * 4.0 + 1.5));

    float r = (fs + 5.0) / 100.0;

    fragColor = vec4(0, 0, 0, 0);

    // Output to screen

    if(uvN.x < (1.4 / 1.5 + r))
    {
        fragColor = vec4(0, 0, 0, 0) * 1.0 - fs2 * 0.5;

        if(length(uv + r / 2.0) < 0.1)
        {
            fragColor = vec4(1, 0, 0, 0) * 1.0 - fs2 * 0.5;
        }
        else
        {
            fragColor = vec4(1) * 1.0 - fs2 * 0.5;
        }

    }

    if(uvN.x < (0.0 / 1.5 + r) && uvN.y < 0.9)
    {
        fragColor = vec4(0, 0, 0, 0);
    }

    if(uvN.y > 0.9 || uvN.y < 0.1)
    {
        fragColor = vec4(0, 0, 0, 0);
    }


}
