#define r(a) mat2(cos(a + vec4(0, 33, 11, 0)))

void mainImage(out vec4 O, vec2 u) {
    vec3  p = iResolution; 
          u = (u + u - p.xy) / p.y;  

    float c, i, d, a, T = iTime;

    O *= 0.;
    while(i++ < 140.){
        p = .7 * vec3(u * a, a - .05);

        p.yz *= r(T * .3);
        p.xz *= r(T * .2); 

        p.x += .01 * cos(T*1.2); 

        c = clamp(dot(p, p), .0004 , .001);

        p *= .2 / c;

        int j, k = 5; 
        while(j++ < k) 
            p.z = abs(p.z), 
            p.xz *= r(3.14/float(k));

        p = abs(cos(p)) * cos(.35 * p.z) * 8. + .001;

        a += d = c * p.y ;

        O += vec2(
                 .004 / exp(10000. * d + .9),
                 .01 / exp(5000. * sqrt(abs(d)) + 1.2)
             ).xyxx * 3.;
    }
}
