void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    float t = iTime;
    vec2 uv = fragCoord/iResolution.xy * 2. - 1.;
    uv.x *= iResolution.x/iResolution.y;
    vec3 col = vec3(0.);
    float c = smoothstep(0.5, 0.4, length(uv));

    uv *= mat2(
        1. + pow(t, uv.y), pow(t, uv.x*cos(t)),
        pow(t, uv.y*sin(t)), 1. + pow(t, uv.x)
    );

    uv *= mat2(
        -sin(t*2.), -cos(t),
        cos(t), -sin(t*2.5)
    );

    uv /= (iMouse.xy/iResolution.xy) - uv;
    col += 1./length(uv*4.);
    col *=vec3(0.2, 0.5, 0.4);

    fragColor = vec4(col,1.0);
}
