// Fork of "El Aleph" by wyatt. https://shadertoy.com/view/MccBRM
// 2024-12-01 17:04:30

Main {

    Q = A(U)/float(iFrame);

}

#define Main void mainImage(out vec4 Q, in vec2 U)
#define R iResolution.xy
#define A(U) texture(iChannel0,(U)/R)
#define ei(a) mat2(cos(a),sin(a),-sin(a),cos(a))
// circular  https://iquilezles.org/articles/smin/
float smin( float a, float b, float k )
{
    k *= 1.0/(1.0-sqrt(0.5));
    float h = max( k-abs(a-b), 0.0 )/k;
    return min(a,b) - k*0.5*(1.0+h-sqrt(1.0-h*(h-2.0)));
}
vec4 hash(vec4 p4)
{
    p4 = fract(p4  * vec4(.1031, .1030, .0973, .1099));
    p4 += dot(p4, p4.wzxy+33.33);
    return fract((p4.xxyz+p4.yzzw)*p4.zywx);
}

float triPrism( vec3 p, vec2 h )
{
  vec3 q = abs(p);
  return max(q.z-.5*h.y,max(q.x*0.866025+p.y*0.5,-p.y)-h.x*0.5);
}

float map (vec3 p) {
    float d = triPrism(p-vec3(0,0,-.253),vec2(.5,.5));
    d = min(d,length(p-vec3(.4,.2,-.152))-.15);
    return min(d,-p.z);

}
vec3 normal (vec3 p) {
    vec2 e = vec2(1e-3,0);
    float m = map(p);
    return normalize(vec3(
        map(p+e.xyy)-m,
        map(p+e.yxy)-m,
        map(p+e.yyx)-m
    ));

}
Main {
    Q = A(U);
    vec4 h = hash(vec4(iFrame,1,1,1));
    float w = h.w;
    U = 2.*(U+h.xy-.5*R)/R.y;
    vec3 p = vec3(0,0,-2.3);
    vec3 d = normalize(vec3(U,3.));
    d.y = -d.y;
    p.yz *= ei(-.8);
    d.yz *= ei(-.8);
    p += h.z*d*map(p);
    for (float i = 0.; i < 200.; i++) {
        float m = map(p);
        p += d*max(abs(m),1e-5);
        if (p.z >-1e-4) {
            //d = normalize(reflect(d,normal(p))*.5+.2*normalize(hash(vec4(R*U,iFrame,i)).xyz*2.-1.));
            d = normalize(hash(vec4(R*U,iFrame,i)).xyz*2.-1.);
            d.z = -abs(d.z);
            p += d*.001;
        } 
        else 
        if (abs(m) < 4e-5) {
            float z = (hash(vec4(R*U,iFrame,i)).z*2.-1.);
            vec3 n = normal(p);
            d = z>0.?reflect(d,n):normalize(d-(.2+.1*w)*n);//refract(d,n,1.);
            p += .001*d;
        }

        if (p.z < -3.) {
            p += d*(p.z+5.);
            p += d*(p.z+5.);
            p += d*(p.z+5.);
            p += d*(p.z+5.);
            p += d*(p.z+5.);
            p += d*(p.z+5.);
            p += d*(p.z+5.);
            p += d*(p.z+5.);
            p += d*(p.z+5.);
            p += d*(p.z+5.);
            Q += max(sin(-2.+6.1*w+vec4(1,2,3,4)),0.)*100.*
                float(length(p.xy-vec2(2,1))<1.);
            Q += .5*vec4(3,1,2,1)*max(sin(-2.+6.2*w+vec4(1,2,3,4)),0.)*100.*
                float(length(p.xy-vec2(-7,-3))<1.);
            Q += vec4(1,2,3,1)*max(sin(-2.+6.2*w+vec4(1,2,3,4)),0.)*100.*
                float(length(p.xy-vec2(7,3))<.5);
            break;
        }
    }


}
