/* Short 2d starfield by misol101 */

void mainImage(out vec4 O, vec2 I)
{
    float h = fract(sin(I.y)*97.)+.1;
    O = vec4(step(abs( mod((iTime+99.) * h * .3, 1.)
        * iResolution.x - I.x), 1.) * h);
}

