void mainImage( out vec4 o, vec2 u )
{
    vec2 R = iResolution.xy;

    // Replace exp2() to change zoom
    u = ceil(exp2(iTime) * (u+u-R) / R.y);

    float a = .99999995; // Checkboard with fractal error
    // a = 1.;           // Checkerboard with no error

    o = vec4(0. == mod(u.x + a * u.y, 2.));
}
